let store = {
    _state: {
        profilePage: {
            posts: [
                { id: 1, message: 'Hello people!', countLikes: '15' },
                { id: 2, message: "I'm hunter", countLikes: '20' },
                { id: 3, message: "I'll stay good programmer", countLikes: '15' },
            ],
            textArea: '',
        },

        dialogsPage: {
            messages: [
                { id: 1, message: 'Hi' },
                { id: 2, message: 'How are you?' },
                { id: 3, message: 'Yo' },
            ],
            dialogs: [
                { id: 1, name: 'Alexander' },
                { id: 2, name: 'Natasha' },
                { id: 3, name: 'Katya' },
                { id: 4, name: 'Maxim' },
                { id: 5, name: 'Pasha' },
            ],
        }

    },
    getState() {
        return this._state
    },
    rerenderEntireTree() {
        console.log('State changed');
    },
    addPost() {
        let newPost = {
            id: this.state.profilePage.posts.length + 1,
            message: this.state.profilePage.textArea,
            countLikes: 0
        }
        this._state.profilePage.posts.push(newPost)
        console.log(this.state.profilePage.posts)
        this.state.profilePage.textArea = ''
    },
    updateNewPostText(text) {
        this._state.profilePage.textArea = text
    },
    subscribe(observer){
        this.rerenderEntireTree = observer
    }
}

export default store
window.store = this._state
