import React from 'react';
import { reduxForm, Field } from 'redux-form';
import { Input } from '../common/FormControls/FormControls';
import { required, maxLengthCreator } from '../../utils/validators/validators';
import { loginThunkCreator, logoutThunkCreator } from '../../redux/authReducer';

//import s from './Header.module.css';
//import { NavLink } from 'react-router-dom';


const maxLength = maxLengthCreator(10);

const LoginForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field 
                    placeholder={"Email"} 
                    name={"email"} 
                    component={Input}
                    validate={[required, maxLength]}
                ></Field>
            </div>
            <div>
                <Field placeholder={"Password"} name={"password"} component={Input} validate={[required, maxLength]} ></Field>
            </div>
            <div>
                <Field type={"checkbox"} name={"rememberMe"} component={Input} validate={[required, maxLength]}></Field>
                    remember me
                </div>
            <div>
                <button>Login</button>
            </div>
        </form>
    )
}

const ReduxLoginForm = reduxForm ({
    form: 'login'
})(LoginForm)

const LoginPage = (props) => {

    const onSubmitted = (formData) => {
        console.log(formData)
    }

    return (
        <div>
            <h1>Login</h1>
            <ReduxLoginForm onSubmit={onSubmitted} />
        </div>
    )
}
export default connect(null, {
    loginThunkCreator,
    logoutThunkCreator
})(LoginPage);
