const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'

let initialState = {
    posts: [
        { id: 1, message: 'Hello people!', countLikes: '15' },
        { id: 2, message: "I'm hunter", countLikes: '20' },
        { id: 3, message: "I'll stay good programmer", countLikes: '15' },
    ],
    textArea: '',
}

const profileReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST:
            console.log("Добавление");
            let newPost = {
                id: state.posts.length + 1,
                message: state.textArea,
                countLikes: 0
            };
            let stateCopy = {...state};
            stateCopy.posts = [...state.posts];
            stateCopy.posts.push(newPost);
            stateCopy.textArea = '';
            console.log(stateCopy);
            return stateCopy;
        case UPDATE_NEW_POST_TEXT:
            console.log("Сработо изменение");
            //let stateCopy = {...state};
            stateCopy.textArea = action.text;
            console.log(stateCopy);
            return stateCopy;
        default:
            return state
    }
}

export const addPostActionCreator = () => {
    return { type: ADD_POST }
}
export const updateNewPostTextActionCreator = (newText) => {
    return { type: UPDATE_NEW_POST_TEXT, text: newText }
}

export default profileReducer;
