import { connect } from 'react-redux';
import { followAC, unfollowAC, setUsersAC, setCurrentPageAC, setTotalCountAC } from '../../redux/usersReducer';
import Users from './Users.jsx'
import axios from 'axios'
import React from 'react'
import preloader from '../../assets/images/5.gif'


class UsersContainer extends React.Component {

    unFollow = (id) => {
        this.props.unfollow(id)
    }
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(response => {
                this.props.setUsers(response.data.items)
                this.props.setTotalCount(response.data.totalCount / 100)
            })
    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPage(pageNumber)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
            .then(response => {
                this.props.setUsers(response.data.items)
            })
    }
    setLoading = (isLoading) => {
        this.props.setLoading(isLoading)
    }

    render() {
        return (
            <>
                {this.props.isLoading ? <img src={preloader}/> : <Users
                    totalCount={this.props.totalCount}
                    pageSize={this.props.pageSize}
                    currentPage={this.props.currentPage}
                    onPageChanged={this.onPageChanged}
                    users={this.props.users}
                    follow={this.props.follow}
                    unFollow={this.unFollow}
                />
                }
            </>
        )
    }
}

let mapStateToProps = (state) => {
    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalCount: state.usersPage.totalCount,
        currentPage: state.usersPage.currentPage,
        isLoading: state.usersPage.isLoading,
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        follow: (id) => { dispatch(followAC(id)) },
        unfollow: (id) => { dispatch(unfollowAC(id)) },
        setUsers: (users) => { dispatch(setUsersAC(users)) },
        setCurrentPage: (pageNumber) => { dispatch(setCurrentPageAC(pageNumber)) },
        setTotalCount: (totalCount) => { dispatch(setTotalCountAC(totalCount)) },
        setLoading: (isLoading) => { dispatch(setLoadingAC(isLoading)) },
    }
}

const UsersMainContainer = connect(mapStateToProps, mapDispatchToProps)(UsersContainer)

export default UsersMainContainer
