import React from 'react'
import s from './Users.module.css'
import userPhoto from '../../assets/images/default_icon.jpg'
import { NavLink } from 'react-router-dom'
import * as axios from 'axios'
import Paginator from '../common/Paginator/Paginator'

let User = (props) => {
    let u = props.user
    return <div className={s.u}>
                    <div>
                        <div>
                            <NavLink to={'/profile/' + u.id}>
                                <img className={s.avatar} src={u.photos.small != null ? u.photos.small : userPhoto} alt='avatar' />
                            </NavLink>
                        </div>
                        {u.followed
                            ? <button disabled={props.followingInProgress.some(i => i === u.id)}
                                onClick={() => {
                                    props.unfollow(u.id)
                                    // props.toggleFollowingProgress(true, u.id)
                                    // usersAPI.follow(u.id)
                                    //     .then(response => {
                                    //         if (response.data.resultCode === 0) {
                                    //             console.log('Работает ДЕЛИТ')
                                    //             props.follow(u.id)
                                    //         }
                                    //         props.toggleFollowingProgress(false, u.id)
                                    //     })

                                }}>Друг</button>
                            : <button disabled={props.followingInProgress.some(i => i === u.id)}
                                onClick={() => {
                                    props.follow(u.id)
                                    // props.toggleFollowingProgress(true, u.id)
                                    // usersAPI.unfollow(u.id)
                                    //     .then(response => {
                                    //         if (response.data.resultCode === 0) {
                                    //             console.log('Работает ПОСТ')
                                    //             props.unFollow(u.id)
                                    //         }
                                    //         props.toggleFollowingProgress(false, u.id)
                                    //     })

                                }}>Не в друзьях</button>
                        }
                    </div>
                    <div>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                        <div>{"u.location.country"}</div>
                        <div>{"u.location.city"}</div>
                    </div>
                </div>

}

export default User
