import React from 'react'
import s from './Users.module.css'
import userPhoto from '../../assets/images/default_icon.jpg'
import { NavLink } from 'react-router-dom'
import * as axios from 'axios'

let Users = (props) => {
    console.log(props)
    let pagesCount = Math.ceil(props.totalCount / props.pageSize)
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }


    return <div>
        <div>
            {
                pages.map(i => {
                    return <span onClick={() => props.onPageChanged(i)} key={i} className={(props.currentPage === i) ? s.selectedPage : ''}>{i}</span>
                })
            }
        </div>

        {props.users.map((user) => {
            return (
                <div className={s.user} key={user.id + user.name}>

                    <div>
                        <div>
                            <NavLink to={'/profile/' + user.id}>
                                <img className={s.avatar} src={user.photos.small != null ? user.photos.small : userPhoto} alt='avatar' />
                            </NavLink>
                        </div>
                        {user.followed
                            ? <button onClick={() => {

                                axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${user.id}`, { 
                                    withCredentials: true,
                                    //headers: 'aefe3d13-769e-4201-8170-a0f001057380'
                                })
                                    .then(response => {
                                        if (response.data.resultCode === 0) {
                                            props.follow(user.id)
                                        }
                                    })

                            }}>Друг</button>
                            : <button onClick={() => {

                                axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${user.id}`, {}, { withCredentials: true })
                                    .then(response => {
                                        if (response.data.resultCode === 0) {
                                            props.unFollow(user.id)
                                        }
                                    })

                            }}>Не в друзьях</button>
                        }
                    </div>
                    <div>
                        <div>{user.name}</div>
                        <div>{user.status}</div>
                        <div>{"user.location.country"}</div>
                        <div>{"user.location.city"}</div>
                    </div>
                </div>
            )
        })
        }
    </div>

}

export default Users
