import React from 'react';
import { addMessageActionCreator, updateNewMessageTextActionCreator } from '../../redux/dialogsReducer';
import Dialogs from './Dialogs';
import { connect } from 'react-redux';


let mapStateToProps = (state) => {
    return {
        dialogs: state.dialogsPage,
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        updateNewMessageTextActionCreator: () => { dispatch(addMessageActionCreator()) },
        addMessageActionCreator: (body) => { dispatch(updateNewMessageTextActionCreator(body)) }
    }
}

const MyDialogsContainer = connect(mapStateToProps, mapDispatchToProps)(Dialogs)

export default MyDialogsContainer
