import { profileAPI } from "../api/profileApi"

const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'
const SET_USER_PROFILE = 'SET_USER_PROFILE'
const SET_USER_STATUS = 'SET_USER_STATUS'
const UPDATE_USER_STATUS = 'UPDATE_USER_STATUS'


let initialState = {
    posts: [
        { id: 1, message: 'Hello people!', countLikes: '15' },
        { id: 2, message: "I'm hunter", countLikes: '20' },
        { id: 3, message: "I'll stay good programmer", countLikes: '15' },
    ],
    textArea: '',
    profile: null,
    status: '',
}

const profileReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST:
            let body = state.textArea;
            return {
                ...state,
                textArea: '',
                posts: [
                    ...state.posts,
                    {
                        id: state.posts.length + 1,
                        message: body,
                        countLikes: 0
                    }
                ]
            }
        case UPDATE_NEW_POST_TEXT:
            return {
                ...state,
                textArea: action.text
            }
        case SET_USER_PROFILE:
            return {
                ...state,
                profile: action.profile
            }
        case SET_USER_STATUS:
            return {
                ...state,
                status: action.status
            }
        default:
            return state
    }
}

export const addPostActionCreator = () => {
    return { type: ADD_POST }
}
export const updateNewPostTextActionCreator = (newText) => {
    return { type: UPDATE_NEW_POST_TEXT, text: newText }
}
export const setUserProfile = (profile) => {
    return { type: SET_USER_PROFILE, profile }
}
export const setUserStatus = (status) => {
    return { type: SET_USER_STATUS, status }
}
export const updateUserStatus = (status) => {
    return { type: UPDATE_USER_STATUS, status }
}
export const getProfileThunkCreator = (userId) => {
    return dispatch => {
        profileAPI.getProfile(userId)
            .then(response => {
                //this.props.setLoading(false)
                dispatch(setUserProfile(response.data))
            })
    }
}
export const getStatusThunkCreator = (userId) => {
    return dispatch => {
        profileAPI.getStatus(userId)
            .then(response => {
                debugger
                //this.props.setLoading(false)
                dispatch(setUserStatus(response.data))
            })
    }
}
export const updateStatusThunkCreator = (status) => {
    return dispatch => {
        profileAPI.updateStatus(status)
            .then(response => {
                debugger
                if (response.data.resultCode === 0) {
                    let {id, login, email } = response.data.data
                    dispatch(updateUserStatus(status))
                }
                dispatch(setUserStatus(response.data))
            })
    }
}

export default profileReducer;
