const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'

let initialState = {
    users: [
        { id: 1, followed: false, fullName: 'Alex', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
        { id: 2, followed: true, fullName: 'Natasha', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
        { id: 3, followed: false, fullName: 'Pavel', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
        { id: 4, followed: false, fullName: 'Yuriy', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
    ]
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map((u) => {
                    if (u.id === action.userId) {
                        return { 
                            ...u, 
                            followed: true 
                        }
                    }
                    return u
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map((u) => {
                    if (u.id === action.userId) {
                        return { 
                            ...u, 
                            followed: false 
                        }
                    }
                    return u
                })
            }
        default:
            return state
    }
}

export const followAC = (userId) => {
    return { type: FOLLOW, userId }
}
export const unfollowAC = (userId) => {
    return { type: UNFOLLOW, userId }
}

export default usersReducer
