import React from 'react'
import s from './Users.module.css'
import * as axios from 'axios'
import userPhoto from '../../assets/images/default_icon.jpg'

let Users = (props) => {

    let pagesCount = Math.ceil(props.totalCount / props.pageSize)
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }

    return <div>
        <div>
            {
                pages.map(i => {
                    return <span onClick={() => this.setNewUsers(i)} key={i} className={(this.props.currentPage === i) ? s.selectedPage : ''}>{i}</span>
                })
            }
        </div>

        {props.users.map((user) => {
            return (
                <div className={s.user} key={user.id + user.name}>

                    <div>
                        <div>
                            <img className={s.avatar} src={user.photos.small != null ? user.photos.small : userPhoto} alt='avatar' />
                        </div>
                        {user.followed
                            ? <button onClick={() => props.follow(user.id)}>Друг</button>
                            : <button onClick={() => this.unfollow(user.id)}>Не в друзьях</button>
                        }
                    </div>
                    <div>
                        <div>{user.name}</div>
                        <div>{user.status}</div>
                        <div>{"user.location.country"}</div>
                        <div>{"user.location.city"}</div>
                    </div>
                </div>
            )
        })
        }
    </div>
}


export default Users
