import React from 'react';
import { reduxForm, Field } from 'redux-form';
import { Input } from '../common/FormControls/FormControls';
import { required, maxLengthCreator } from '../../utils/validators/validators';
import { loginThunkCreator } from '../../redux/authReducer';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

//import s from './Header.module.css';
//import { NavLink } from 'react-router-dom';


const maxLength = maxLengthCreator(3);

const LoginForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field 
                    placeholder={"Email"} 
                    name={"email"} 
                    component={Input}
                    validate={[required, maxLength]}
                ></Field>
            </div>
            <div>
                <Field placeholder={"Password"} name={"password"} type={'password'}
                component={Input} validate={[required, maxLength]} ></Field>
            </div>
            <div>
                <Field type={"checkbox"} name={"rememberMe"} component={Input} validate={[required, maxLength]}></Field>
                    remember me
                </div>
            <div>
                <button>Login</button>
            </div>
        </form>
    )
}

const ReduxLoginForm = reduxForm ({
    form: 'login'
})(LoginForm)

const LoginPage = (props) => {

    const onSubmitted = (formData) => {
        console.log(formData)
        props.loginThunkCreator(formData.email, formData.password, formData.rememberMe)
    }

    if (props.isAuth) {
        return <Redirect to={'/profile'} />
    }

    return (
        <div>
            <h1>Login</h1>
            <ReduxLoginForm onSubmit={onSubmitted} />
        </div>
    )
}

const mapStateToProps = (state) => {
    return { isAuth: state.auth.isAuth }
}

export default connect(mapStateToProps, {
    loginThunkCreator,
    logoutThunkCreator
})(LoginPage);
