import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from './DialogItem/DialogItem';
import Message from './Message/Message';
import { addMessageActionCreator, updateNewMessageTextActionCreator } from '../../redux/dialogsReducer';

const Dialogs = (props) => {


    let state = props

    let newMessageElement = React.createRef()
    let addMessage = () => {
        props.addMessageActionCreator()
    }
    let onMessageChange = (event) => {
        props.updateNewMessageTextActionCreator(event.target.value)
        //props.store.dispatch(updateNewMessageTextActionCreator(event.target.value))
    }

    return (
        <div className={s.dialogs}>
            <div className={s.messages}>
                {
                    state.dialogsPage.dialogs.map((d) => {
                        return (<DialogItem name={d.name} id={d.id} />)
                    })
                }
            </div>
            <div className={s.messages}>
                {
                    state.dialogsPage.messages.map((m) => {
                        return (<Message message={m.message} id={m.id} />)
                    })
                }
            </div>
            <div className={s.messages_block_c}>
                <div className={s.messagesNew}>
                    <h3>New message</h3>
                    <div>
                        <textarea 
                            ref={newMessageElement}
                            onChange={onMessageChange}
                            value={state.textAreaMessage}
                            placeholder="Enter your message"
                        >
                        </textarea>
                    </div>
                    <div className={s.newMessage__button}>
                        <button 
                            onClick={addMessage}
                        >Add message</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dialogs
