import React from 'react'

let Users = (props) => {
    console.log(props)


    let unfollow = (id) => {
        props.unfollow(id)
    }
    // props.setUsers([
    //     { id: 1, followed: false, fullName: 'Alex', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
    //     { id: 2, followed: true, fullName: 'Natasha', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
    //     { id: 3, followed: false, fullName: 'Pavel', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
    //     { id: 4, followed: false, fullName: 'Yuriy', status: 'I am boss', location: { city: 'Moscow', country: 'Russia' } },
    // ])

    return (
        <div>
            {props.users.map((user) => {
                return (
                    <div>
                        <div>
                            <div>
                                <img src="https://ukranews.com/upload/news/2019/10/09/5c818ce3bbcd6------------------------------------------------_410x272.png?v=1" alt='avatar' width='100px'/>
                            </div>
                            { user.followed 
                                ? <button onClick={() => props.follow(user.id)}>Друг</button> 
                                : <button onClick={() => unfollow(user.id)}>Не в друзьях</button>  
                            }

                        </div>
                        <div>
                            <div>Имя</div>
                            <div>Сообщение</div>
                            <div>Страна</div>
                            <div>Город</div>
                        </div>
                    </div>
                )
            })
            }
        </div>
    )
}

export default Users
