import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from './DialogItem/DialogItem';
import Message from './Message/Message';
import { updateNewMessageTextActionCreator, addMessageActionCreator } from '../../redux/state';

const Dialogs = (props) => {
    console.log(props)
    let newMessageElement = React.createRef()
    let addMessage = () => {
        props.dispatch(addMessageActionCreator())
    }
    let onMessageChange = () => {
        let newText = newMessageElement.current.value;
        props.dispatch(updateNewMessageTextActionCreator(newText))
    }

    return (
        <div className={s.dialogs}>
            <div className={s.messages}>
                {
                    props.state.dialogs.map((d) => {
                        return (<DialogItem name={d.name} id={d.id} />)
                    })
                }
            </div>
            <div className={s.messages}>
                {
                    props.state.messages.map((m) => {
                        return (<Message message={m.message} id={m.id} />)
                    })
                }
            </div>
            <div>
                <div className={s.messagesNew}>
                    <h3>New message</h3>
                    <div>
                        <textarea 
                            ref={newMessageElement}
                            onChange={onMessageChange}
                            value={props.state.textAreaMessage}
                            placeholder="Enter your message"
                        >
                        </textarea>
                    </div>
                    <div className={s.newMessage__button}>
                        <button 
                            onClick={addMessage}
                        >Add message</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dialogs
