const ADD_MESSAGE = 'ADD-MESSAGE'
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT'

let initialState = {
    messages: [
        { id: 1, message: 'Hi' },
        { id: 2, message: 'How are you?' },
        { id: 3, message: 'Yo' },
    ],
    dialogs: [
        { id: 1, name: 'Alexander' },
        { id: 2, name: 'Natasha' },
        { id: 3, name: 'Katya' },
        { id: 4, name: 'Maxim' },
        { id: 5, name: 'Pasha' },
    ],
    textAreaMessage: '',
}

const dialogsReducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_MESSAGE: {
            let body = state.textAreaMessage
            return {
                ...state,
                textAreaMessage: '',
                messages: [
                    ...state.messages,
                    {
                        id: state.messages.length + 1,
                        message: body,
                    }
                ]
            }
        }
        case UPDATE_NEW_MESSAGE_TEXT: {
            return {
                ...state,
                textAreaMessage: action.text
            }
        }
        default:
            return state
    }
}

export const addMessageActionCreator = (formData) => {
    return { type: ADD_MESSAGE, formData }
}
export const updateNewMessageTextActionCreator = (newText) => {
    return { type: UPDATE_NEW_MESSAGE_TEXT, text: newText }
}

export default dialogsReducer;
