const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'

const ADD_MESSAGE = 'ADD-MESSAGE'
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT'

let store = {
    _state: {
        profilePage: {
            posts: [
                { id: 1, message: 'Hello people!', countLikes: '15' },
                { id: 2, message: "I'm hunter", countLikes: '20' },
                { id: 3, message: "I'll stay good programmer", countLikes: '15' },
            ],
            textArea: '',
        },
        dialogsPage: {
            messages: [
                { id: 1, message: 'Hi' },
                { id: 2, message: 'How are you?' },
                { id: 3, message: 'Yo' },
            ],
            dialogs: [
                { id: 1, name: 'Alexander' },
                { id: 2, name: 'Natasha' },
                { id: 3, name: 'Katya' },
                { id: 4, name: 'Maxim' },
                { id: 5, name: 'Pasha' },
            ],
            textAreaMessage: '',
        }

    },

    getState() {
        return this._state
    },
    _callSubscriber() {
        console.log('State changed');
    }, subscribe(observer) {
        this._callSubscriber = observer
    },

    dispatch(action) { // { type: 'ADD-POST'}
        switch (action.type) {
            case ADD_POST:
                let newPost = {
                    id: this._state.profilePage.posts.length + 1,
                    message: this._state.profilePage.textArea,
                    countLikes: 0
                };
                this._state.profilePage.posts.push(newPost);
                this._state.profilePage.textArea = '';
                this._callSubscriber(this._state);
                break;
            case UPDATE_NEW_POST_TEXT:
                this._state.profilePage.textArea = action.text;
                this._callSubscriber(this._state);
                break;
            case ADD_MESSAGE:
                let newMessage = {
                    id: this._state.dialogsPage.messages.length + 1,
                    message: this._state.dialogsPage.textAreaMessage,
                };
                this._state.dialogsPage.messages.push(newMessage);
                this._state.dialogsPage.textAreaMessage = '';
                this._callSubscriber(this._state);
                break;
            case UPDATE_NEW_MESSAGE_TEXT:
                this._state.dialogsPage.textAreaMessage = action.text;
                this._callSubscriber(this._state);
                break;
            default:
                console.log("Sorry");
        }
    }
}

export const addPostActionCreator = () => {
    return { type: ADD_POST }
}
export const updateNewPostTextActionCreator = (newText) => {
    return { type: UPDATE_NEW_POST_TEXT, text: newText }
}

export const addMessageActionCreator = () => {
    return { type: ADD_MESSAGE }
}
export const updateNewMessageTextActionCreator = (newText) => {
    return { type: UPDATE_NEW_MESSAGE_TEXT, text: newText }
}

export default store
window.store = store
