export const Input = ({ input, meta, ...props }) => {
    const hasError = meta.error && meta.touched
    return (
        <div className={s.form_control + " " + (hasError && s.error)}>
            <div>
                <input {...input} {...props} />
            </div>
            {hasError && <span>s{meta.error}</span>}
        </div>
    )
}
