import React from 'react'
import s from './Users.module.css'
import * as axios from 'axios'
import userPhoto from '../../assets/images/default_icon.jpg'

let Users = (props) => {

    let unfollow = (id) => {
        props.unfollow(id)
    }

    let getUsers = () => {

        if (props.users.length === 0) {

            axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {
                debugger
                props.setUsers(response.data.items)
            })
        }
    }

    return (
        <div>
            {props.users.map((user) => {
                return (
                    <div className={s.user}>
                        <div>
                            <div>
                                <img className={s.avatar} src={user.photos.small != null ? user.photos.small : userPhoto} alt='avatar' />
                            </div>
                            {user.followed
                                ? <button onClick={() => props.follow(user.id)}>Друг</button>
                                : <button onClick={() => unfollow(user.id)}>Не в друзьях</button>
                            }
                        </div>
                        <div>
                            <div>{user.name}</div>
                            <div>{user.status}</div>
                            <div>{"user.location.country"}</div>
                            <div>{"user.location.city"}</div>
                        </div>
                    </div>
                )
            })
            }
        </div>
    )
}

export default Users
