//import React from 'react';
import { addMessageActionCreator, updateNewMessageTextActionCreator } from '../../redux/dialogsReducer';
import Dialogs from './Dialogs';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';


let mapStateToProps = (state) => {
    return {
        dialogs: state.dialogsPage,
        isAuth: state.auth.isAuth,
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        addMessage: () => { dispatch(addMessageActionCreator()) },
        updateNewMessageText: (body) => { dispatch(updateNewMessageTextActionCreator(body)) }
    }
}
let AuthRedirectContainer = (props) => {
    if (props.isAuth === false) {
        return (
           <Redirect to='/login'/>  
        )

    }
    return <Dialogs {...props} />
}

const MyDialogsContainer = connect(mapStateToProps, mapDispatchToProps)(AuthRedirectContainer)

export default MyDialogsContainer
