import React from 'react'

let Users = (props) => {
    console.log(props)

    let follow = (id) => {
        props.follow(id)
    }
    let unfollow = (id) => {
        props.unfollow(id)
    }
    let setUsers = (users) => {
        props.setUsers(users)
    }

    return (
        <div>
            {props.users.map((user) => {
                return (
                    <div>
                        <div>
                            <div>Аватарка</div>
                            { user.followed 
                                ? <button onClick={follow}>Follow</button> 
                                : <button onClick={unfollow}>Unfollow</button>  
                            }

                        </div>
                        <div>
                            <div>Имя</div>
                            <div>Сообщение</div>
                            <div>Страна</div>
                            <div>Город</div>
                        </div>
                    </div>
                )
            })
            }
        </div>
    )
}

export default Users
