import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post'

let addPostActionCreator = () => {
    return {type: 'ADD-POST'}
}
let updateNewPostTextActionCreator = (newText) => {
    return {type: 'UPDATE-NEW-POST-TEXT', text: newText}
}

const MyPosts = (props) => {
    console.log(props)

    let addPost = () => {
        props.dispatch(addPostActionCreator())
    }

    let onPostChange = () => {
        let newPostElement = React.createRef()
        let newText = newPostElement.current.value;
        props.dispatch(updateNewPostTextActionCreator(newText))
    }
    //console.log(props)
    return (
        <div className={s.postsBlock}>
            <h3>My posts</h3>    
            <div>
                <div>
                    <textarea 
                        ref={newPostElement}
                        onChange={onPostChange}
                        value={props.posts.textArea}
                    >
                    </textarea>
                </div>
                <div className={s.myPost__button}>
                    <button onClick={addPost}>Add post</button>
                    {/* <button>Remove</button> */}
                </div>
            </div>
            <div className={s.posts}>
                {
                    props.posts.posts.map((p) => <Post message={p.message} countLikes={p.countLikes} />)
                }
                {/* <Post message={postData[0].message} countLikes={postData[0].countLikes} />
                <Post message="I'm hunter" countLikes='20' /> */}
            </div>
        </div>
    )
}

export default MyPosts
