import React from 'react'
import s from './Users.module.css'
import userPhoto from '../../assets/images/default_icon.jpg'
import { NavLink } from 'react-router-dom'
import * as axios from 'axios'
import Paginator from '../common/Paginator/Paginator'


let Users = (props) => {
    let pagesCount = Math.ceil(props.totalCount / props.pageSize)
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    return <div>
        {/* <div>
            {
                pages.map(i => {
                    return <span onClick={() => props.onPageChanged(i)} key={i} className={(props.currentPage === i) ? s.selectedPage : ''}>{i}</span>
                })
            }
        </div> */}
        <Paginator  {...props} />

        {props.users.map((user) => {
            return (
                <div className={s.user} key={user.id + user.name}>

                    <div>
                        <div>
                            <NavLink to={'/profile/' + user.id}>
                                <img className={s.avatar} src={user.photos.small != null ? user.photos.small : userPhoto} alt='avatar' />
                            </NavLink>
                        </div>
                        {user.followed
                            ? <button disabled={props.followingInProgress.some(i => i === user.id)}
                                onClick={() => {
                                    props.unfollow(user.id)
                                    // props.toggleFollowingProgress(true, user.id)
                                    // usersAPI.follow(user.id)
                                    //     .then(response => {
                                    //         if (response.data.resultCode === 0) {
                                    //             console.log('Работает ДЕЛИТ')
                                    //             props.follow(user.id)
                                    //         }
                                    //         props.toggleFollowingProgress(false, user.id)
                                    //     })

                                }}>Друг</button>
                            : <button disabled={props.followingInProgress.some(i => i === user.id)}
                                onClick={() => {
                                    props.follow(user.id)
                                    // props.toggleFollowingProgress(true, user.id)
                                    // usersAPI.unfollow(user.id)
                                    //     .then(response => {
                                    //         if (response.data.resultCode === 0) {
                                    //             console.log('Работает ПОСТ')
                                    //             props.unFollow(user.id)
                                    //         }
                                    //         props.toggleFollowingProgress(false, user.id)
                                    //     })

                                }}>Не в друзьях</button>
                        }
                    </div>
                    <div>
                        <div>{user.name}</div>
                        <div>{user.status}</div>
                        <div>{"user.location.country"}</div>
                        <div>{"user.location.city"}</div>
                    </div>
                </div>
            )
        })
        }
    </div>

}

export default Users
