import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import { reduxForm, Field } from 'redux-form';
//import { addPostActionCreator, updateNewPostTextActionCreator } from '../../../redux/profileReducer';


const MyPosts = (props) => {

    let onAddPost = () => {
        props.addPost()
    }
    let onPostChange = (event) => {
        props.updateNewPostTextActionCreator(event.target.value)
    }
    return (
        <div className={s.postsBlock}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea
                        // ref={newPostElement}
                        onChange={onPostChange}
                        value={props.textArea}
                    >
                    </textarea>
                </div>
                <div className={s.myPost__button}>
                    <button onClick={onAddPost}>Add post</button>
                    {/* <button>Remove</button> */}
                </div>
            </div>
            <div className={s.posts}>
                {props.posts.map((p) => <Post key={p.id} message={p.message} countLikes={p.countLikes} />)}
            </div>
        </div>
    )
}

const PostsForm = (props) => {
    return (
        <form>
            <Field placeholder="Enter your message" name="message" component="textarea"></Field>
            {/* <textarea
                // ref={newPostElement}
                //onChange={onPostChange}
                value={props.textArea}
            >
            </textarea> */}
            <div className={s.myPost__button}>
                <button 
                // onClick={onAddPost}
                >Add post</button>
                {/* <button>Remove</button> */}
            </div>
        </form>
    )
}

const ReduxDialogsForm = reduxForm({
    form: 'message'
})(PostsForm)


export default MyPosts
